select from xinwentongzhi where 1=1 and biaoti like concat('%',#{biaoti,jdbcType=VARCHAR},'%') and leibie like concat('%',#{leibie,jdbcType=VARCHAR},'%') and leibie like concat('%',#{lb,jdbcType=VARCHAR},'%') and neirong like concat('%',#{neirong,jdbcType=VARCHAR},'%') and shouyetupian like concat('%',#{shouyetupian,jdbcType=VARCHAR},'%') and dianjilv like concat('%',#{dianjilv,jdbcType=VARCHAR},'%') and tianjiaren like concat('%',#{tianjiaren,jdbcType=VARCHAR},'%')
order by id desc
limit #{pageno},#{pageSize}
